package assignment1;

public class Question8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int A = 1,B = 2 ;
		System.out.print("Before Swaping :-\nA = "+ A +"\nB = "+ B );
		B=A+B;//A = 1,B = 3 
		A=B-A;//A = 2,B = 3 
		B=B-A;//A = 2,B = 1 
		System.out.print("\nAfter Swaping :-\nA = "+ A +"\nB = "+ B );


	}

}
